import React from "react";
import { Link } from "react-router-dom";

const Cart = props => {
  const removeItem = e => {
    const identification = e.target.dataset.id;
    props.setCart(props.cart.filter(item => item.id !== identification));
  };

  const lessAmount = e => {
    const identification = e.target.dataset.id;
    props.setCart(
      props.cart.map(item => {
        if (item.id === identification) item.amount -= 1;
        return item;
      })
    );
  };

  return (
    <div>
      <nav>
        <Link to="/">
          <button className="closeCart-btn">
            <i class="fa fa-times" aria-hidden="true"></i>
          </button>
        </Link>
      </nav>
      <h1>Cart</h1>

      <ul>
        {props.cart.map(item => {
          return (
            <li>
              <h2>{item.title}</h2>
              <img src={item.img} alt={item.title} />
              <h3>Price: â‚¬{item.price}</h3>
              <span
                className="remove-item"
                onClick={removeItem}
                data-id={item.id}
              >
                remove
              </span>
              <button
                className="windowUp"
                onClick={lessAmount}
                data-id={props.id}
              >
                up
              </button>
              <p className="item-amount">{item.amount}</p>
              <button className="windowDown">down</button>
            </li>
          );
        })}
      </ul>
    </div>
  );
};

export default Cart;
