import React from "react";

import Product from "./Product";

const Products = props => {
  const getProducts = async () => {
    const response = await fetch("/api/products", {
      method: "GET",
      headers: {
        "Content-Type": "application/json;charset=utf-8",
      },
    });
    const data = await response.json();
    console.log(data.items[0].fields.price);
    const items = data.items;
    items.map(item => {
      props.setProducts(...props.products, {
        price: item.items.fields.price,
        title: item.items.fields.title,
        img: item.items.fields.image.url,
      });
    });
    console.log(props.products);
  };

  return (
    <div>
      <Product />
      <button onClick={getProducts}>Products</button>
    </div>
  );
};

export default Products;
