import React from "react";

const Cart = props => {
  return (
    <div>
      <h1>Cart</h1>
      <ul>
        {props.cart.map(item => {
          return (
            <li>
              <h2>{item.title}</h2>
              <img src={item.img} alt={item.title} />
              <h3>Price: â‚¬{item.price}</h3>
              <span
                className="remove-item"
                onClick={removeItem}
                data-id={item.id}
              >
                remove
              </span>
              <button
                className="windowUp"
                onClick={addAmount}
                data-id={item.id}
              >
                up
              </button>
              <p className="item-amount">{item.amount}</p>
              <button
                className="windowDown"
                onClick={lessAmount}
                data-id={item.id}
              >
                down
              </button>
            </li>
          );
        })}
      </ul>
    </div>
  );
};

export default Cart;
