import React from "react";

const Product = props => {
  const addToCart = e => {
    const identification = e.target.dataset.id;
    if (props.cart.some(item => item.id === identification))
      props.setCart(...props.cart, item.amount + 1);
    const cart = {
      amount: 1,
      title: props.title,
      price: props.price,
      img: props.img,
      id: props.id,
    };
    props.cart.push(cart);
    console.log(props.cart);
  };

  return (
    <div>
      <h1>{props.title}</h1>
      <img src={props.img} alt={props.title} />
      <h2>Price: â‚¬ {props.price}</h2>
      <button onClick={addToCart} data-set={item.id}>
        Add it
      </button>
    </div>
  );
};

export default Product;
